mapper.select:
		SELECT *
  	FROM (
      	SELECT ROWNUM rnum, t.*
      	FROM (
          	SELECT *
          	FROM INQUIRIES
          	WHERE SUBJECT LIKE '%' || #{search} || '%'
             		OR MESSAGE LIKE '%' || #{search} || '%'
          	ORDER BY CREATED_AT DESC
      	) t
      	WHERE ROWNUM <= #{endRow}
  	)
  	WHERE rnum >= #{startRow}
