mapper.select:
		SELECT *
      FROM (
          SELECT ROWNUM rnum, t.*
          FROM (
              SELECT *
              FROM NOTICES
              WHERE TITLE LIKE '%' || #{search} || '%'
                 OR CONTENT LIKE '%' || #{search} || '%'
              ORDER BY POSTED_AT DESC
          ) t
          WHERE ROWNUM <= #{endRow}
      )
      WHERE rnum >= #{startRow}

mapper.update:
		UPDATE NOTICES
        SET title = #{title},
            content = #{content}
            visible = #{visible}
            posted_at = #{posted_at}
            expires_at = #{expires_at}
            admin_id = #{adminId}
            updated_at = NOW()
        WHERE id = #{id}

UPDATE NOTICES
SET view_count = NVL(view_count, 0) + 1
WHERE id = #{id}
